mapper.select:
SELECT h.*,v.name AS vendor_name
FROM hotel h
LEFT JOIN vendor v ON h.vendor_id = v.id

    
        AND h.id = #{id}
    
    
        AND h.vendor_id = #{vendorId}
    
    
        AND h.name LIKE concat('%',#{name},'%')
    
    
        AND h.create_time BETWEEN #{startTime} AND #{endTime}
    

ORDER BY h.id DESC

    LIMIT #{current},#{size}

        SELECT COUNT(*)
        FROM hotel h
        
            
                AND h.id = #{id}
            
            
                AND h.vendor_id = #{vendorId}
            
            
                AND h.name LIKE concat('%',#{name},'%')
            
            
                AND h.create_time BETWEEN #{startTime} AND #{endTime}
