mapper.select:
SELECT v.*,u.user_name
FROM vendor v
LEFT JOIN user u ON u.id = v.user_id

    
        AND v.id = #{id}
    
    
        AND v.user_id = #{userId}
    
    
        AND v.name LIKE concat('%',#{name},'%')
    
    
        AND v.is_audit = #{isAudit}
    
    
        AND v.status = #{status}
    
    
        AND v.create_time BETWEEN #{startTime} AND #{endTime}
    

ORDER BY v.id DESC

    LIMIT #{current},#{size}

        SELECT COUNT(*)
        FROM vendor v
        
            
                AND v.id = #{id}
            
            
                AND v.user_id = #{userId}
            
            
                AND v.name LIKE concat('%',#{name},'%')
            
            
                AND v.is_audit = #{isAudit}
            
            
                AND v.status = #{status}
            
            
                AND v.create_time BETWEEN #{startTime} AND #{endTime}
