mapper.select:
SELECT hr.*,h.name AS hotel_name
FROM hotel_room hr
LEFT JOIN hotel h ON hr.hotel_id = h.id

    
        AND hr.id = #{id}
    
    
        AND hr.hotel_id = #{hotelId}
    
    
        AND hr.name LIKE concat('%',#{name},'%')
    
    
        AND hr.create_time BETWEEN #{startTime} AND #{endTime}
    

ORDER BY hr.id DESC

    LIMIT #{current},#{size}

        SELECT COUNT(*)
        FROM hotel_room hr
        
            
                AND hr.id = #{id}
            
            
                AND hr.hotel_id = #{hotelId}
            
            
                AND hr.name LIKE concat('%',#{name},'%')
            
            
                AND hr.create_time BETWEEN #{startTime} AND #{endTime}
