SELECT sto.*, u.user_name, s.id AS scenic_id, s.name AS scenic_name, st.detail, st.number, st.price, st.discount FROM scenic_ticket_order sto LEFT JOIN user u ON sto.user_id = u.id LEFT JOIN scenic_ticket st ON sto.ticket_id = st.id LEFT JOIN scenic s ON st.scenic_id = s.id WHERE sto.ticket_id IN
#{id}
AND sto.pay_status = #{scenicTicketOrderQueryDto.payStatus}
AND sto.create_time BETWEEN #{scenicTicketOrderQueryDto.startTime} AND #{scenicTicketOrderQueryDto.endTime}
AND sto.user_id = #{scenicTicketOrderQueryDto.userId}
ORDER BY sto.id DESC
LIMIT #{scenicTicketOrderQueryDto.current}, #{scenicTicketOrderQueryDto.size}
